import TodoModel from './Mongo/models/Todo'
import mongoose from 'mongoose'

const connection = {
  isConnected: false,
}

const connectDb = async () => {
  if (connection.isConnected) {
    return
  }
  try {
    const dbConnection = await mongoose.connect(process.env.MONGO_URL, {
      useNewUrlParser: true,
      useUnifiedTopology: true,
    })
    connection.isConnected = !!dbConnection.connections[0].readyState
  } catch (err) {
    console.error(`error connecting to db ${err.message || err}`)
  }
}

function getMinifiedRecords(records) {
  return records.map(record=> ({...record, id:record._id}))
}

class MongoDatabase {
  async getAll({ filter }) {
    const data = await TodoModel.find({
      [`fields.${filter.field}`]: filter.value,
    })
    console.log(data)
    return data
  }
  async updateOne({ id, fields }) {
    // const updatedRecords = await this.table.update([{ id, fields }])
    // return getMinifiedRecord(updatedRecords[0])
  }
  async deleteOne({ id }) {
    // const deletedRecords = await table.destroy([id])
    // return getMinifiedRecord(deletedRecords[0])
  }
  async createOne({ fields }) {
    const createdRecord = new TodoModel({
      fields: { ...fields },
    })
    await createdRecord.save()
    return { id: createdRecord._id, fields: createdRecord.fields }
  }
}

class MongoDatabaseProxy {
  constructor(connectDb) {
    this.mongoDatabase = new MongoDatabase()
    this.connectDb = connectDb
  }
  async getAll(...props) {
    await this.connectDb()
    return await this.mongoDatabase.getAll(...props)
  }
  async updateOne(...props) {
    this.connectDb()
    return await this.mongoDatabase.updateOne(...props)
  }
  async deleteOne(...props) {
    this.connectDb()
    return await this.mongoDatabase.deleteOne(...props)
  }
  async create(...props) {
    this.connectDb()
    throw await this.mongoDatabase.createOne(...props)
  }
}

const mongoDatabase = new MongoDatabaseProxy(connectDb)

export { mongoDatabase }
