import Head from 'next/head'
import Navbar from '../components/Navbar'
import Todo from '../components/Todo'
import { getMinifiedRecords, table } from './api/utils/Airtable'

export default function Home({ initialTodos, err }) {
  return (
    <div>
      <Head>
        <title>Todo</title>
        <link rel='icon' href='/favicon.ico' />
      </Head>
      <Navbar />
      <main>
        <h1>Hello</h1>
        {initialTodos.map(todo => (
          <Todo key={todo.id} todo={todo} />
        ))}
      </main>
    </div>
  )
}

export async function getServerSideProps(context) {
  try {
    const todos = await table.select({}).sort([{field:"date", direction: "asc"}]).firstPage()
    return { props: { initialTodos: getMinifiedRecords(todos) } }
  } catch (e) {
    return { props: { err: 'Something went wrong' } }
  }
}
