import { table, getMinifiedRecords } from './utils/Airtable'

export default async (req, res) => {
  try {
    let records = await table.select({}).firstPage()
    records = records.sort((a, b) => a.fields.date > b.fields.date)
    const minifiedRecords = getMinifiedRecords(records)
    return res.json(minifiedRecords)
  } catch (e) {
    console.log(e)
    return res.status(500).json({ mgs: 'Somethig went wrong' })
  }
}
