import React from 'react'

export default function Todo({ todo:{fields:{description, completed}} }) {
  return (
    <ul className='bg-white flex items-center shadow-lg rounded-lg my-2 py-2 px-4'>
      <input
        type='checkbox'
        name='completed'
        id='completed'
        checked={completed}
        className='mr-2 form-checkbox h-5 w-5'
      />
      <p
        className={`flex-1 text-gray-800 ${
          completed ? 'line-through' : ''
        }`}
      >
        {description}
      </p>
      <button type="button" className="text-sm">Delete</button>
    </ul>
  )
}
